const initialState = {
  data: [
    {
      id: 1,
      name: "project 1",
      feature: [
        { id: 1, name: "Feature 1" },
        { id: 2, name: "Feature 2" },
      ],
    },
    {
      id: 2,
      name: "project 2",
      feature: [
        { id: 1, name: "Second Feature 1" },
        { id: 2, name: "Second Feature 2" },
      ],
    },
  ],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "NEW_A_CONTENT": {
      let count = state.data.length;
      count++;
      let newData = state.data.concat({ id: count, name: action.payload });

      return {
        data: newData,
      };
    }
    case "DEL_A_CONTENT": {
      let del = state.data.filter((item) => item.id != action.payload);
      console.log(del[0]);
      return {
        data: del,
      };
    }
    case "CHA_A_CONTENT": {
      const changed = state.data.map((item) => {
        if (item.id == action.id) {
          return { id: action.id, name: action.payload };
        } else {
          return item;
        }
      });
      return {
        ...state,
        data: changed,
      };
    }
    case "FEATUREADD_A_CONTENT": {
      const chg = state.data[action.objnum].feature.map((item) => {
        if (item.id == action.id) {
          return { id: action.id, name: action.payload };
        } else {
          return item;
        }
      });

      // var he = state.data[action.objnum].feature.splice(0,0,chg);
      console.log(he);
      // return {
      //   ...state,
      //   data: chg,
      // };
    }
    default:
      return state;
  }
}

export default rootReducer;
