const initialState = {
  data: [
    { id: 1, name: "project 1" },
    { id: 2, name: "project 2" },
  ],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "ADD_A_CONTENT": {
      return {};
    }
    case "DEL_A_CONTENT": {
      clg
      return {};
    }
    case "CHA_A_CONTENT": {
      console.log(action.payload, action.id);
      const changed = state.data.map((item) => {
        if (item.id == action.id) {
          return { id: action.id, name: action.payload };
        } else {
          return item;
        }
      });
      console.log(changed);
      return {
        ...state,
        data: changed,
      };
    }
    default:
      return state;
  }
}

export default rootReducer;
