const initialState = {
  data: [
    { id: 1, name: "project 1" },
    { id: 2, name: "project 2" },
  ],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "NEW_A_CONTENT": 
    {
      console.log(state.data);
      let newData = state.data.concat({ id: 3, name: "project 3" })
// let count = state.data.length();
// count++;
// console.log(count);
      return {
        data: newData,
      };
    }
    case "DEL_A_CONTENT": {
      let del = state.data.filter((item) => item.id != action.payload);
      console.log(del[0]);
      return {
        data: [del[0]],
      };
    }
    case "CHA_A_CONTENT": {
      const changed = state.data.map((item) => {
        if (item.id == action.id) {
          return { id: action.id, name: action.payload };
        } else {
          return item;
        }
      });
      return {
        ...state,
        data: changed,
      };
    }
    default:
      return state;
  }
}

export default rootReducer;
