const initialState = {
  data: [{id:1,name:'project 1'},{id:2,name:'project 2'}],

};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "ADD_A_CONTENT": {
      return {};
    }
    case "SUB_A_CONTENT": {
      return {};
    }
    case "CHA_A_CONTENT": {
      // const change = data.map((value)=>{
      //    return()
      // });
      console.log(action.payload,action.id);
      return {
        ...state,
        data:action.payload
      };
    }
    default:
      return state;
  }
}

export default rootReducer;
